mapper.select:
SELECT t1.*,IFNULL(t2.time ,t1.CREATEDATE)latestDate,IFNULL(t2.articleCount,0)articleCount FROM (SELECT * FROM t_t_favorites c WHERE c.UID=#{vo.uid} and `STATUS`='1' ) t1 LEFT JOIN (SELECT Max(CTIME) time ,count(*) articleCount, FID FROM t_t_collect c WHERE c.UID=#{vo.uid} and `STATUS`='1' GROUP BY FID) t2 ON t1.id=t2.FID
visual= #{vo.visual}
ORDER BY latestDate DESC
